package Lec3;

public class Pattern_p30 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		int row = 1;
//		int k = 1;
		int star =1;
		while(row<=n) {
			int i = n;
			while(i>=1) {
			
				System.out.print(i+" ");
				i--;
			}
				
			
		
			System.out.println();
			row++;
			star++;
			
		}

	}

}
