package Lec7;

public class Data_type_2_char_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		System.out.println((int)(ch));
		int i = 1;
		i++; //i = (int)(i+1);
		i+= 1; //i = (int)(i+1);
		ch++;  // ch = (char)(ch+1);
		ch = (char) (ch + 1);
		ch += 1; //ch = (char)(ch+1)
		System.out.println(ch);
				

	}

}
